mapper.update:
update ums_user
set status = '2000'
where username = #{userCode}
  and pwd_error_cnt >= #{maxPasswordErrorTimes}
  and status != '2000'

mapper.select:
select a.user_id, a.user_phone, a.username, a.icon, a.email, a.nick_name, a.note, a.create_time, a.login_time, a.status, if(a.status = '1000', '有效', '已冻结') statusName, a.pwd_error_cnt, (select group_concat(r.name separator ',') from ums_user_role_relation ur, ums_role r where a.user_id = ur.user_id and r.id = ur.role_id and r.status = '1000' group by ur.user_id) role_name from ums_user a where 1 = 1
and CONCAT(a.nick_name, a.user_phone, a.username) like CONCAT('%', #{value}, '%')
and a.status = #{status}

select b.id, b.name, b.id code
from ums_user_role_relation a,
     ums_role b
where a.role_id = b.id
  and b.status = '1000'
  and a.user_id = #{userId}

select b.id, b.name, b.level, b.sort 'index', b.path uri, b.parent_id, b.id code from ums_role_menu_relation a, ums_menu b where a.menu_id = b.id and a.role_id in
#{item.id}
order by b.sort

select b.id, b.name, b.code code, a.menu_id
from ums_menu_func_relation a,
     ums_func b
        where a.func_id = b.id
          and a.menu_id in

    #{item.id}

select b.id, b.name, b.url uri, b.method, b.id code
from ums_func_resource_relation a,
     ums_resource b
        where a.resource_id = b.id
          and a.func_id in

    #{item.id}
